package com.reyurnible.network;import android.app.ActivityManager;import android.content.Context;import com.android.volley.RequestQueue;import com.android.volley.toolbox.Volley;//RequestQueuesを管理するクラス。//シングルトン制御public class VolleyHelper{    public static final Object lock = new Object();        //private static RequestQueue requestQueue;    private static RequestQueue requestQueue;    private static CustomImageLoader imageLoader;        /**     * RequestQueueのシングルトン生成     * @param context アプリケーションコンテキスト     * @return     */    public static RequestQueue getRequestQueue(final Context context) {        synchronized (lock) {            if (requestQueue == null) {                requestQueue = Volley.newRequestQueue(context);            }            return requestQueue;        }    }        public static void RequestCancell(Object tag){    	synchronized (lock) {            if (requestQueue != null) {                requestQueue.cancelAll(tag);            }        }    }        public static void RequestStart(){    	synchronized (lock) {            if (requestQueue != null) {                requestQueue.start();            }        }    }        public static void RequestStop(){    	synchronized (lock) {            if (requestQueue != null) {                requestQueue.stop();            }        }    }        public static CustomImageLoader getImageLoader(final Context context) {        if (imageLoader == null) {            imageLoader = new CustomImageLoader(getRequestQueue(context), new BitmapCache());        }        return imageLoader;    }}